import java.util.*;

public class Bank {
  private Account masterAccount = new Account();
  public int maxAccountNumber = -1;
  public ArrayList<Account> listOfAccounts = new ArrayList<>();
  public int getNextAccountNumber(){
    addMaxAccount();
    return maxAccountNumber;
  }
  public void addMaxAccount(){
    maxAccountNumber += 1;
  }
  public void addAcount(Account account){
    listOfAccounts.add(account);
  }
  public Account findAccount(int accountNumber){
    return listOfAccounts.get(accountNumber);
  }
  public Account getMainAccount(){
    return masterAccount;
  }

}




public class Account{
    private int accountNumber;
    private int balance;
    public Account(int balance){
      accountNumber = getNextAccountNumber();
      balance = this.balance;
      this.addAccount(this);
    }
  }
