public int findFreeBlock(int period, int minutes){
  int consecutiveFreeMinutes = 0;
  for (int i = 0; i < 59 - minutes; i++){
     if (isMinuteFree(period, minutes)){
      consecutiveFreeMinutes++;
    } 
    if (consecutiveFreeMinutes == minutes){
      return i - consecutiveFreeMinutes ;
    } else {
      consecutiveFreeMinutes = 0;
    }
  }

  return -1;
}





public boolean makeAppointment(int startPeriod, int endPeriod, int duration){
  for (int i = startPeriod; i < endPeriod; i++){
    if (findFreeBlock(i, duration) != -1){
      reserveBlock(i, findFreeBlock(i, duration), duration);
      return true
    }
  }
  return false;
}
