public class Sign {

  String text;
  int width;

  public Sign(String text, int width) {
    this.text = text;
    this.width = width;
  }
  public int numberOfLines(){
    return this.width % this.text.length();
  }
  public String getLines(){
    String tempString = "";
    if (this.text.equals("")){
      return null;
    }
    if (this.text.length() <= width){
      return this.text;
    }
    for (int i = 0; i < numberOfLines(); i++){
        tempString += this.text(0, width);
    tempString += ";";

    }

  }

}
